import React from 'react';
import axios from 'axios';

const ModalPesoVolume = ({
  pedidoParaConcluir, peso, setPeso, volume, setVolume, setMostrarModalPesoVolume,
  setPedidoParaConcluir, setPedidos, setPedidosConcluidos, setMensagem, carregarPedidos
}) => {
  const calcularTempo = (inicio, fim = new Date().toLocaleString('pt-BR', { timeZone: 'America/Sao_Paulo' })) => {
    const inicioDate = new Date(inicio);
    const fimDate = new Date(fim);
    const diffMs = fimDate - inicioDate;
    return diffMs < 0 ? 0 : Math.floor(diffMs / (1000 * 60 * 60));
  };

  const enviarConclusao = async () => {
    if (!peso || !volume) {
      setMensagem('Preencha peso e volume.');
      return;
    }
    const pesoNum = parseFloat(peso);
    const volumeNum = parseFloat(volume);
    if (isNaN(pesoNum) || pesoNum < 0 || isNaN(volumeNum) || volumeNum < 0) {
      setMensagem('Peso e volume devem ser números positivos.');
      return;
    }
    if (!pedidoParaConcluir || !pedidoParaConcluir.id) {
      console.error('pedidoParaConcluir inválido:', pedidoParaConcluir);
      setMensagem('Erro: Pedido inválido para conclusão.');
      return;
    }

    const dataConclusao = new Date().toLocaleString('pt-BR', { timeZone: 'America/Sao_Paulo' });
    const dataHoraAtual = new Date().toLocaleString('pt-BR', { timeZone: 'America/Sao_Paulo' });
    const quantidadeEntregueDigitada = parseInt(pedidoParaConcluir.quantidadeEntregueDigitada) || 0;
    const quantidadeEntregueExistente = parseInt(pedidoParaConcluir.quantidadeEntregue) || 0;
    const quantidadeEntregueTotal = quantidadeEntregueExistente + quantidadeEntregueDigitada; // Soma apenas aqui

    const pedidoAtualizado = {
      ...pedidoParaConcluir,
      quantidadeEntregue: quantidadeEntregueTotal,
      peso: pesoNum,
      volume: volumeNum,
      status: pedidoParaConcluir.quantidadePedido - quantidadeEntregueTotal <= 0 ? 'concluido' : 'andamento',
      dataConclusao: pedidoParaConcluir.quantidadePedido - quantidadeEntregueTotal <= 0 ? dataConclusao : pedidoParaConcluir.dataConclusao,
      tempo: pedidoParaConcluir.quantidadePedido - quantidadeEntregueTotal <= 0 ? calcularTempo(pedidoParaConcluir.inicio, dataConclusao) : calcularTempo(pedidoParaConcluir.inicio),
    };

    if (quantidadeEntregueTotal > pedidoParaConcluir.quantidadePedido) {
      setMensagem('A quantidade entregue total não pode exceder a quantidade pedida.');
      return;
    }

    try {
      const resposta = await axios.put(`http://localhost:5000/pedidos/${pedidoParaConcluir.id}`, pedidoAtualizado);
      const pedidoConcluido = { ...resposta.data, tempo: calcularTempo(resposta.data.inicio, resposta.data.dataConclusao || undefined) };
      await axios.post('http://localhost:5000/enviar-email', {
        pedido: pedidoConcluido,
        observacao: `Atualização\n- Quantidade Pedido: ${pedidoConcluido.quantidadePedido}\n- Quantidade Entregue: ${quantidadeEntregueDigitada}\n- Peso: ${peso}\n- Volume: ${volume}\n- Data e Hora: ${dataHoraAtual}`,
      });

      if (pedidoConcluido.status === 'concluido') {
        setPedidos((prev) => prev.filter((p) => p.id !== pedidoConcluido.id));
        setPedidosConcluidos((prev) => [...prev, pedidoConcluido]);
        setMensagem('Pedido concluído e e-mail enviado!');
      } else {
        setPedidos((prev) => prev.map((p) => (p.id === pedidoConcluido.id ? pedidoConcluido : p)));
        setMensagem('Quantidade entregue atualizada e e-mail enviado!');
      }

      setMostrarModalPesoVolume(false);
      setPeso('');
      setVolume('');
      setPedidoParaConcluir(null);
    } catch (error) {
      console.error('Erro no enviarConclusao:', {
        message: error.message,
        response: error.response ? error.response.data : null,
        status: error.response ? error.response.status : null,
        pedidoId: pedidoParaConcluir.id,
      });
      setMensagem('Erro ao processar pedido: ' + (error.response ? error.response.data.message || error.response.statusText : error.message));
      await carregarPedidos();
    }
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <h3>Preencha Peso e Volume para {pedidoParaConcluir.numeroOS}</h3>
        <div>
          <label htmlFor="peso">Peso</label>
          <input type="number" id="peso" name="peso" value={peso} onChange={(e) => setPeso(e.target.value)} min="0" step="0.01" />
        </div>
        <div>
          <label htmlFor="volume">Volume</label>
          <input type="number" id="volume" name="volume" value={volume} onChange={(e) => setVolume(e.target.value)} min="0" step="0.01" />
        </div>
        <div>
          <label htmlFor="quantidadeEntregueDigitada">Quantidade Entregue</label>
          <input
            type="number"
            id="quantidadeEntregueDigitada"
            name="quantidadeEntregueDigitada"
            value={pedidoParaConcluir?.quantidadeEntregueDigitada || ''}
            onChange={(e) => setPedidoParaConcluir({ ...pedidoParaConcluir, quantidadeEntregueDigitada: e.target.value })}
            min="0"
          />
        </div>
        <div className="modal-buttons">
          <button className="btn-enviar" onClick={enviarConclusao}>Enviar</button>
          <button className="btn-cancelar" onClick={() => { setMostrarModalPesoVolume(false); setPedidoParaConcluir(null); }}>Cancelar</button>
        </div>
      </div>
    </div>
  );
};

export default ModalPesoVolume;
