import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';
import jsPDF from 'jspdf';
import autoTable from 'jspdf-autotable';
import PedidoForm from './components/pedidoForm';
import PedidoTable from './components/PedidoTable';
import ModalObservacao from './components/ModalObservacao';
import ModalPesoVolume from './components/ModalPesoVolume';
import Busca from './components/Busca';

function App() {
  const [pedidos, setPedidos] = useState([]);
  const [pedidosAndamento, setPedidosAndamento] = useState([]);
  const [pedidosConcluidos, setPedidosConcluidos] = useState([]);
  const [novoPedido, setNovoPedido] = useState({
    empresa: '',
    numeroOS: '',
    dataEntrada: '',
    previsaoEntrega: '',
    responsavel: '',
    codigoDesenho: '',
    quantidadePedido: '',
    quantidadeEntregue: '',
    status: 'novo',
    inicio: new Date().toLocaleString('pt-BR', { timeZone: 'America/Sao_Paulo' }),
    tempo: 0,
    peso: null,
    volume: null,
    dataConclusao: null,
    pausado: 0, // Novo campo
  });
  const [pedidoParaEditar, setPedidoParaEditar] = useState(null);
  const [mensagem, setMensagem] = useState('');
  const [busca, setBusca] = useState('');
  const [mostrarModal, setMostrarModal] = useState(false);
  const [pedidoSelecionado, setPedidoSelecionado] = useState(null);
  const [observacao, setObservacao] = useState('');
  const [peso, setPeso] = useState('');
  const [volume, setVolume] = useState('');
  const [mostrarModalPesoVolume, setMostrarModalPesoVolume] = useState(false);
  const [pedidoParaConcluir, setPedidoParaConcluir] = useState(null);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);

  const carregarPedidos = async (dados = null) => {
    try {
      const response = dados ? { data: dados } : await axios.get('http://localhost:5000/pedidos');
      const pedidosAtualizados = response.data.map((pedido) => {
        return {
          ...pedido,
          tempo: pedido.status === 'concluido' && pedido.dataConclusao
            ? calcularTempo(pedido.inicio, pedido.dataConclusao)
            : pedido.tempo || calcularTempo(pedido.inicio),
        };
      });
      setPedidos(pedidosAtualizados.filter((p) => p.status === 'andamento'));
      setPedidosAndamento(pedidosAtualizados.filter((p) => p.status === 'novo'));
      setPedidosConcluidos(pedidosAtualizados.filter((p) => p.status === 'concluido'));
    } catch (error) {
      console.error('Erro ao carregar pedidos:', error);
      setMensagem('Erro ao carregar pedidos: ' + error.message);
    }
  };

  useEffect(() => {
    carregarPedidos();
    const intervalo = setInterval(() => {
      setPedidos((prev) => prev.map((p) => ({
        ...p,
        tempo: p.pausado ? p.tempo : calcularTempo(p.inicio), // Não atualiza tempo se pausado
      })));
      setPedidosAndamento((prev) => prev.map((p) => ({ ...p, tempo: calcularTempo(p.inicio) })));
    }, 60000);
    return () => clearInterval(intervalo);
  }, []);

  const calcularTempo = (inicio, fim = new Date().toLocaleString('pt-BR', { timeZone: 'America/Sao_Paulo' })) => {
    const inicioDate = new Date(inicio);
    const fimDate = new Date(fim);
    const diffMs = fimDate - inicioDate;
    return diffMs < 0 ? 0 : diffMs / (1000 * 60 * 60);
  };

  const exportarPDF = () => {
    const doc = new jsPDF();
    const headersAndamento = ['ID', 'Empresa', 'Nº OS', 'Código', 'Qtd Pedido', 'Qtd Entregue', 'Saldo', 'Data Entrada', 'Previsão', 'Responsável', 'Início', 'Tempo (h)'];
    const headersNovos = ['ID', 'Empresa', 'Nº OS', 'Código', 'Qtd Pedido', 'Qtd Entregue', 'Data Entrada', 'Previsão', 'Responsável', 'Início', 'Tempo (h)'];
    const headersConcluidos = ['ID', 'Empresa', 'Nº OS', 'Código', 'Qtd Pedido', 'Qtd Entregue', 'Data Entrada', 'Previsão', 'Responsável', 'Início e Conclusão', 'Tempo Total (h)'];

    const addTable = (title, data, headers) => {
      doc.text(title, 14, 20);
      autoTable(doc, {
        head: [headers],
        body: data.map((p) => [
          p.id,
          p.empresa,
          p.numeroOS,
          p.codigoDesenho,
          p.quantidadePedido,
          p.quantidadeEntregue,
          p.status === 'andamento' ? p.quantidadePedido - p.quantidadeEntregue : '',
          p.dataEntrada ? new Date(p.dataEntrada).toLocaleDateString('pt-BR') : 'Não informado',
          p.previsaoEntrega ? new Date(p.previsaoEntrega).toLocaleDateString('pt-BR') : 'Não informado',
          p.responsavel,
          p.status === 'concluido' ? `${p.inicio}\n${p.dataConclusao}` : p.inicio,
          formatarTempo(p.tempo),
        ]),
        startY: 30,
      });
    };

    addTable('Pedidos em Andamento', pedidos, headersAndamento);
    doc.addPage();
    addTable('Pedidos Novos', pedidosAndamento, headersNovos);
    doc.addPage();
    addTable('Pedidos Concluídos', pedidosConcluidos, headersConcluidos);
    doc.save('pedidos_controle_producao.pdf');
  };

  const formatarTempo = (tempo) => {
    if (isNaN(tempo)) return '0 minutos';
    const horas = Math.floor(tempo);
    const minutos = Math.round((tempo - horas) * 60);
    return `${horas > 0 ? `${horas} hora${horas > 1 ? 's' : ''} e ` : ''}${minutos} minuto${minutos !== 1 ? 's' : ''}`;
  };

  const moverParaAndamento = async (id) => {
    const pedido = [...pedidos, ...pedidosAndamento, ...pedidosConcluidos].find((p) => p.id === id);
    if (pedido) {
      const pedidoAtualizado = { ...pedido, status: 'andamento', tempo: pedido.tempo || calcularTempo(pedido.inicio) };
      try {
        const resposta = await axios.put(`http://localhost:5000/pedidos/${id}`, pedidoAtualizado);
        const pedidoMovido = { ...resposta.data, tempo: pedidoAtualizado.tempo };
        await axios.post('http://localhost:5000/enviar-email', { pedido: pedidoMovido, observacao: '' });
        setPedidosAndamento((prev) => prev.filter((p) => p.id !== id));
        setPedidos((prev) => [...prev.filter((p) => p.id !== id), pedidoMovido]);
        setPedidosConcluidos((prev) => prev.filter((p) => p.id !== id));
        setMensagem('Pedido movido para "Em Andamento" e e-mail enviado.');
      } catch (error) {
        setMensagem('Erro ao mover pedido: ' + (error.response ? error.response.data.message : error.message));
        await carregarPedidos();
      }
    }
  };

  const pausarPedido = async (id) => {
    const pedido = pedidos.find((p) => p.id === id);
    if (pedido) {
      const pedidoPausado = { ...pedido, pausado: 1 };
      try {
        await axios.put(`http://localhost:5000/pedidos/${id}`, pedidoPausado);
        setPedidos((prev) => prev.map((p) => (p.id === id ? pedidoPausado : p)));
        setMensagem('Pedido pausado com sucesso.');
      } catch (error) {
        setMensagem('Erro ao pausar pedido: ' + error.message);
        await carregarPedidos();
      }
    }
  };

  const retomarPedido = async (id) => {
    const pedido = pedidos.find((p) => p.id === id);
    if (pedido) {
      const pedidoRetomado = { ...pedido, pausado: 0 };
      try {
        await axios.put(`http://localhost:5000/pedidos/${id}`, pedidoRetomado);
        setPedidos((prev) => prev.map((p) => (p.id === id ? pedidoRetomado : p)));
        setMensagem('Pedido retomado com sucesso.');
      } catch (error) {
        setMensagem('Erro ao retomar pedido: ' + error.message);
        await carregarPedidos();
      }
    }
  };

  return (
    <div className="container">
      <div className="header">
        <img src="/logoNF.jpg" alt="Logo" className="logo" />
        <h1>Controle de Produção</h1>
        <button className="btn-desenho" onClick={() => window.open('https://drive.google.com/drive/folders/1vzemVbLeotHD0xirxUPsvNb9qBccQFcd?usp=sharing', '_blank')}>
          DESENHO
        </button>
      </div>

      {mensagem && <p className={mensagem.includes('Erro') ? 'erro' : 'sucesso'}>{mensagem}</p>}

      <button className="btn-adicionar" onClick={() => setMostrarFormulario(true)}>Adicionar Pedido Novo</button>
      {mostrarFormulario && (
        <PedidoForm
          novoPedido={novoPedido}
          setNovoPedido={setNovoPedido}
          pedidoParaEditar={pedidoParaEditar}
          setPedidoParaEditar={setPedidoParaEditar}
          setMostrarFormulario={setMostrarFormulario}
          setMensagem={setMensagem}
          carregarPedidos={carregarPedidos}
          setPedidos={setPedidos}
          setPedidosAndamento={setPedidosAndamento}
          setPedidosConcluidos={setPedidosConcluidos}
          setMostrarModalPesoVolume={setMostrarModalPesoVolume}
          setPedidoParaConcluir={setPedidoParaConcluir}
          moverParaAndamento={moverParaAndamento}
        />
      )}

      <Busca
        busca={busca}
        setBusca={setBusca}
        carregarPedidos={carregarPedidos}
        todosPedidos={[...pedidos, ...pedidosAndamento, ...pedidosConcluidos]}
        exportarPDF={exportarPDF}
      />

      <h2>Pedidos em Andamento</h2>
      <PedidoTable
        pedidos={pedidos}
        tipo="andamento"
        setPedidos={setPedidos}
        setPedidosAndamento={setPedidosAndamento}
        setPedidosConcluidos={setPedidosConcluidos}
        setMensagem={setMensagem}
        setPedidoParaEditar={setPedidoParaEditar}
        setNovoPedido={setNovoPedido}
        setMostrarFormulario={setMostrarFormulario}
        setMostrarModal={setMostrarModal}
        setPedidoSelecionado={setPedidoSelecionado}
        setMostrarModalPesoVolume={setMostrarModalPesoVolume}
        setPedidoParaConcluir={setPedidoParaConcluir}
        busca={busca}
        carregarPedidos={carregarPedidos}
        moverParaAndamento={moverParaAndamento}
        pausarPedido={pausarPedido} // Passando função de pausar
        retomarPedido={retomarPedido} // Passando função de retomar
      />

      <h2>Pedidos Novos</h2>
      <PedidoTable
        pedidos={pedidosAndamento}
        tipo="novo"
        setPedidos={setPedidos}
        setPedidosAndamento={setPedidosAndamento}
        setPedidosConcluidos={setPedidosConcluidos}
        setMensagem={setMensagem}
        setPedidoParaEditar={setPedidoParaEditar}
        setNovoPedido={setNovoPedido}
        setMostrarFormulario={setMostrarFormulario}
        setMostrarModal={setMostrarModal}
        setPedidoSelecionado={setPedidoSelecionado}
        setMostrarModalPesoVolume={setMostrarModalPesoVolume}
        setPedidoParaConcluir={setPedidoParaConcluir}
        busca={busca}
        carregarPedidos={carregarPedidos}
        moverParaAndamento={moverParaAndamento}
      />

      <h2>Pedidos Concluídos</h2>
      <PedidoTable
        pedidos={pedidosConcluidos}
        tipo="concluido"
        setPedidos={setPedidos}
        setPedidosAndamento={setPedidosAndamento}
        setPedidosConcluidos={setPedidosConcluidos}
        setMensagem={setMensagem}
        setPedidoParaEditar={setPedidoParaEditar}
        setNovoPedido={setNovoPedido}
        setMostrarFormulario={setMostrarFormulario}
        setMostrarModal={setMostrarModal}
        setPedidoSelecionado={setPedidoSelecionado}
        setMostrarModalPesoVolume={setMostrarModalPesoVolume}
        setPedidoParaConcluir={setPedidoParaConcluir}
        busca={busca}
        carregarPedidos={carregarPedidos}
      />

      {mostrarModal && (
        <ModalObservacao
          pedidoSelecionado={pedidoSelecionado}
          observacao={observacao}
          setObservacao={setObservacao}
          setMostrarModal={setMostrarModal}
          setMensagem={setMensagem}
        />
      )}

      {mostrarModalPesoVolume && (
        <ModalPesoVolume
          pedidoParaConcluir={pedidoParaConcluir}
          peso={peso}
          setPeso={setPeso}
          volume={volume}
          setVolume={setVolume}
          setMostrarModalPesoVolume={setMostrarModalPesoVolume}
          setPedidoParaConcluir={setPedidoParaConcluir}
          setPedidos={setPedidos}
          setPedidosConcluidos={setPedidosConcluidos}
          setMensagem={setMensagem}
          carregarPedidos={carregarPedidos}
        />
      )}
    </div>
  );
}

export default App;
