import React from 'react';
import axios from 'axios';

const Busca = ({ busca, setBusca, carregarPedidos, todosPedidos, exportarPDF }) => {
  const buscarPedidos = async () => {
    try {
      const response = await axios.get('http://localhost:5000/pedidos');
      const dados = response.data.map((pedido) => ({
        ...pedido,
        tempo: pedido.status === 'concluido' && pedido.dataConclusao
          ? calcularTempo(pedido.inicio, pedido.dataConclusao)
          : calcularTempo(pedido.inicio),
      }));
      await carregarPedidos(dados);
      setMensagem(busca ? 'Busca concluída!' : 'Digite algo para buscar.');
    } catch (error) {
      console.error('Erro ao buscar pedidos:', error);
      setMensagem('Erro ao buscar pedidos: ' + error.message);
    }
  };

  const calcularTempo = (inicio, fim = new Date().toISOString()) => {
    const diffMs = new Date(fim) - new Date(inicio);
    return diffMs < 0 ? 0 : diffMs / (1000 * 60 * 60);
  };

  const filtrarPedidos = (lista) => {
    if (!busca) return lista;
    return lista.filter((pedido) =>
      pedido.empresa.toLowerCase().includes(busca.toLowerCase()) ||
      pedido.numeroOS.toLowerCase().includes(busca.toLowerCase())
    );
  };

  return (
    <div className="busca">
      <div className="busca-container">
        <input
          type="text"
          id="buscaInput"
          name="buscaInput"
          placeholder="Buscar por Empresa ou Nº OS"
          value={busca}
          onChange={(e) => setBusca(e.target.value)}
        />
        <button className="btn-exportar" onClick={exportarPDF}>Exportar PDF</button>
      </div>
      <button className="btn-buscar" onClick={buscarPedidos}>Buscar</button>
      {busca && (
        <ul className="lista-suspensa">
          {filtrarPedidos(todosPedidos).length > 0 ? (
            filtrarPedidos(todosPedidos).map((pedido) => (
              <li key={pedido.id}>
                {pedido.empresa} - {pedido.numeroOS} ({pedido.status})
              </li>
            ))
          ) : (
            <li>Nenhum pedido encontrado</li>
          )}
        </ul>
      )}
    </div>
  );
};

export default Busca;
