import React from 'react';
import axios from 'axios';

const ModalPesoVolume = ({
  pedidoParaConcluir,
  peso,
  setPeso,
  volume,
  setVolume,
  setMostrarModalPesoVolume,
  setPedidoParaConcluir,
  setPedidos,
  setPedidosConcluidos,
  setMensagem,
  carregarPedidos,
}) => {
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Sempre no modo de conclusão (editar peso e volume)
      if (!peso || !volume) {
        setMensagem('Peso e volume são obrigatórios.');
        return;
      }
      const dataConclusao = new Date().toLocaleString('pt-BR', { timeZone: 'America/Sao_Paulo' });
      const pedidoConcluido = { ...pedidoParaConcluir, status: 'concluido', peso: parseFloat(peso), volume: parseFloat(volume), dataConclusao };
      const resposta = await axios.put(`http://localhost:5000/pedidos/${pedidoParaConcluir.id}`, pedidoConcluido);
      await axios.post('http://localhost:5000/enviar-email', { pedido: resposta.data, observacao: '' });
      setPedidos((prev) => prev.filter((p) => p.id !== pedidoConcluido.id));
      setPedidosConcluidos((prev) => [...prev, resposta.data]);
      setMensagem('Pedido concluído e e-mail enviado!');
      setPeso('');
      setVolume('');
      setMostrarModalPesoVolume(false);
      setPedidoParaConcluir(null);
    } catch (error) {
      setMensagem('Erro ao processar: ' + (error.response?.data.message || error.message));
      await carregarPedidos();
    }
  };

  return (
    <div className="modal">
      <div className="modal-content">
        <h2>Concluir Pedido</h2>
        <form onSubmit={handleSubmit}>
          {/* Lista de itens com quantidades pedidas, entregues e a entregar */}
          <div>
            <h3>Itens do Pedido</h3>
            {Array.isArray(pedidoParaConcluir?.itens) && pedidoParaConcluir.itens.length > 0 ? (
              <table className="tabela-itens">
                <thead>
                  <tr>
                    <th>Código do Desenho</th>
                    <th>Quantidade Pedida</th>
                    <th>Quantidade Entregue</th>
                    <th>A Entregar</th>
                  </tr>
                </thead>
                <tbody>
                  {pedidoParaConcluir.itens.map((item, index) => (
                    <tr key={index}>
                      <td>{item.codigoDesenho}</td>
                      <td>{item.quantidadePedido}</td>
                      <td>{item.quantidadeEntregue || 0}</td>
                      <td>{(item.quantidadePedido || 0) - (item.quantidadeEntregue || 0)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            ) : (
              <p>Sem itens para exibir.</p>
            )}
          </div>

          {/* Campos de peso e volume */}
          <div>
            <label htmlFor="peso">Peso (kg) *</label>
            <input
              type="number"
              id="peso"
              value={peso}
              onChange={(e) => setPeso(e.target.value)}
              min="0"
              step="0.01"
              required
            />
          </div>
          <div>
            <label htmlFor="volume">Volume (m³) *</label>
            <input
              type="number"
              id="volume"
              value={volume}
              onChange={(e) => setVolume(e.target.value)}
              min="0"
              step="0.01"
              required
            />
          </div>

          <div className="modal-buttons">
            <button type="submit">Concluir</button>
            <button
              type="button"
              onClick={() => {
                setPeso('');
                setVolume('');
                setMostrarModalPesoVolume(false);
                setPedidoParaConcluir(null);
              }}
            >
              Cancelar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ModalPesoVolume;
