import React from 'react';
import axios from 'axios';

const PedidoTable = ({
  pedidos,
  tipo,
  setPedidos,
  setPedidosAndamento,
  setPedidosConcluidos,
  setMensagem,
  setPedidoParaEditar,
  setNovoPedido,
  setMostrarFormulario,
  setMostrarModal,
  setPedidoSelecionado,
  setMostrarModalPesoVolume,
  setPedidoParaConcluir,
  busca,
  carregarPedidos,
  moverParaAndamento,
  pausarPedido,
  retomarPedido,
}) => {
  const formatarData = (data) => {
    return data ? new Date(data).toLocaleDateString('pt-BR', { day: '2-digit', month: '2-digit', year: 'numeric' }) : 'Não informado';
  };

  const formatarDataHora = (data) => {
    return data || 'Não informado';
  };

  const formatarTempo = (tempo) => {
    if (isNaN(tempo)) return '0h 0m';
    const horas = Math.floor(tempo);
    const minutos = Math.round((tempo - horas) * 60);
    return `${horas}h ${minutos}m`; // Formato preciso
  };

  const calcularTempo = (inicio, fim) => {
    const inicioDate = new Date(inicio);
    const fimDate = new Date(fim);
    const diffMs = fimDate - inicioDate;
    return diffMs < 0 ? 0 : diffMs / (1000 * 60 * 60); // Retorna em horas com precisão
  };

  const editarPedido = (pedido) => {
    setPedidoParaEditar(pedido);
    setNovoPedido({ ...pedido });
    setMostrarFormulario(true);
  };

  const excluirPedido = async (id) => {
    if (window.confirm('Tem certeza que deseja excluir este pedido?')) {
      try {
        await axios.delete(`http://localhost:5000/pedidos/${id}`);
        setPedidos((prev) => prev.filter((p) => p.id !== id));
        setPedidosAndamento((prev) => prev.filter((p) => p.id !== id));
        setPedidosConcluidos((prev) => prev.filter((p) => p.id !== id));
        setMensagem('Pedido excluído com sucesso!');
      } catch (error) {
        console.error('Erro no excluirPedido:', error);
        setMensagem('Erro ao excluir pedido: ' + error.message);
      }
    }
  };

  const concluirPedido = (id) => {
    const pedidoAndamento = pedidos.find((p) => p.id === id);
    if (!pedidoAndamento) {
      setMensagem('Erro: Pedido não encontrado para conclusão.');
      return;
    }
    const agora = new Date().toLocaleString('pt-BR', { timeZone: 'America/Sao_Paulo' });
    const tempoFinal = calcularTempo(pedidoAndamento.inicio, agora);
    const novoPedidoParaConcluir = { ...pedidoAndamento, status: 'concluido', dataConclusao: agora, tempo: tempoFinal };
    setPedidoParaConcluir(novoPedidoParaConcluir);
    setMostrarModalPesoVolume(true);
  };

  const abrirModalObservacao = (pedido) => {
    setPedidoSelecionado(pedido);
    setMostrarModal(true);
  };

  const isPastDue = (previsaoEntrega) => {
    if (!previsaoEntrega) return false;
    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const dueDate = new Date(previsaoEntrega);
    dueDate.setHours(0, 0, 0, 0);
    const isLate = dueDate < today;
    console.log(`Previsão: ${previsaoEntrega}, Hoje: ${today.toISOString()}, Atrasado: ${isLate}`);
    return isLate && tipo !== 'concluido';
  };

  const filtrarPedidos = (lista) => {
    if (!busca) return lista;
    return lista.filter((pedido) =>
      pedido.empresa.toLowerCase().includes(busca.toLowerCase()) ||
      pedido.numeroOS.toLowerCase().includes(busca.toLowerCase())
    );
  };

  const headers = {
    andamento: [
      'Empresa', 'Número da OS', 'Código do Desenho', 'Quantidade Pedido', 'Quantidade Entregue',
      'Saldo Restante', 'Data de Entrada', 'Previsão de Entrega', 'Responsável', 'Início', 'Tempo (h)', 'Ações'
    ],
    novo: [
      'Empresa', 'Número da OS', 'Código do Desenho', 'Quantidade Pedido', 'Quantidade Entregue',
      'Data de Entrada', 'Previsão de Entrega', 'Responsável', 'Início', 'Tempo (h)', 'Ações'
    ],
    concluido: [
      'Empresa', 'Número da OS', 'Código do Desenho', 'Quantidade Pedido', 'Quantidade Entregue',
      'Data de Entrada', 'Previsão de Entrega', 'Responsável', 'Início e Conclusão', 'Tempo Total (h)', 'Ações'
    ]
  };

  return (
    <table className={`tabela ${tipo === 'novo' ? 'novos' : ''}`}>
      <thead>
        <tr>
          {headers[tipo].map((header, index) => (
            <th key={index}>
              {header.split(' ').map((word, i) => (
                <React.Fragment key={i}>
                  {word}
                  {i < header.split(' ').length - 1 && <br />}
                </React.Fragment>
              ))}
            </th>
          ))}
        </tr>
      </thead>
      <tbody>
        {filtrarPedidos(pedidos).map((pedido) => (
          <tr key={pedido.id} className={isPastDue(pedido.previsaoEntrega) ? 'atrasado' : ''}>
            <td>{pedido.empresa}</td>
            <td>{pedido.numeroOS}</td>
            <td>{pedido.codigoDesenho}</td>
            <td>{pedido.quantidadePedido}</td>
            <td>{pedido.quantidadeEntregue}</td>
            {tipo === 'andamento' && <td>{pedido.quantidadePedido - pedido.quantidadeEntregue}</td>}
            <td>{formatarData(pedido.dataEntrada)}</td>
            <td>
              {formatarData(pedido.previsaoEntrega)}
              {isPastDue(pedido.previsaoEntrega) && <span className="atrasado-icon">⚠️</span>}
            </td>
            <td>{pedido.responsavel}</td>
            <td className="data-hora">
              {tipo === 'concluido' ? (
                <>
                  {formatarDataHora(pedido.inicio)}<br />
                  {formatarDataHora(pedido.dataConclusao)}
                </>
              ) : (
                formatarDataHora(pedido.inicio)
              )}
            </td>
            <td className={tipo === 'andamento' && pedido.pausado ? 'tempo-pausado' : ''}>
              {formatarTempo(pedido.tempo)}
            </td>
            <td>
              <div className="btn-container">
                <div className="btn-row">
                  {tipo === 'andamento' && (
                    <button className="btn-concluir" onClick={() => concluirPedido(pedido.id)}>Concluir</button>
                  )}
                  {tipo === 'novo' && (
                    <button className="btn-mover" onClick={() => moverParaAndamento(pedido.id)}>Andamento</button>
                  )}
                  <button className="btn-editar" onClick={() => editarPedido(pedido)}>Editar</button>
                </div>
                <div className="btn-row">
                  <button className="btn-excluir" onClick={() => excluirPedido(pedido.id)}>Excluir</button>
                  <button className="btn-observacao" onClick={() => abrirModalObservacao(pedido)}>Obs</button>
                </div>
                {tipo === 'andamento' && (
                  <div className="btn-row">
                    {pedido.pausado ? (
                      <button className="btn-retomar" onClick={() => retomarPedido(pedido.id)}>Retomar</button>
                    ) : (
                      <button className="btn-pausar" onClick={() => pausarPedido(pedido.id)}>Pausar</button>
                    )}
                  </div>
                )}
              </div>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default PedidoTable;
