import React from 'react';
import axios from 'axios';

const PedidoForm = ({
  novoPedido, setNovoPedido, pedidoParaEditar, setPedidoParaEditar,
  setMostrarFormulario, setMensagem, carregarPedidos, setPedidos,
  setPedidosAndamento, setPedidosConcluidos, setMostrarModalPesoVolume,
  setPedidoParaConcluir
}) => {
  const handleSubmit = async (e) => {
    e.preventDefault();
    const camposObrigatorios = ['empresa', 'numeroOS', 'dataEntrada', 'previsaoEntrega'];
    const erros = camposObrigatorios.filter(campo => !novoPedido[campo]);
    if (erros.length > 0) {
      setMensagem(`Preencha os campos obrigatórios: ${erros.join(', ')}`);
      return;
    }

    const quantidadePedido = parseInt(novoPedido.quantidadePedido) || 0;
    let quantidadeEntregue = parseInt(novoPedido.quantidadeEntregue) || 0;

    if (quantidadeEntregue > quantidadePedido) {
      window.alert('A quantidade entregue não pode exceder a quantidade pedida!');
      return;
    }

    const pedidoParaEnviar = {
      ...novoPedido,
      quantidadePedido,
      quantidadeEntregue,
      tempo: calcularTempo(novoPedido.inicio),
    };

    try {
      let resposta;
      if (pedidoParaEditar) {
        if (novoPedido.status === 'andamento' && quantidadeEntregue !== pedidoParaEditar.quantidadeEntregue) {
          const novaQuantidadeEntregue = quantidadeEntregue; // Usar o valor digitado diretamente
          if (novaQuantidadeEntregue > quantidadePedido) {
            window.alert('A quantidade entregue não pode exceder a quantidade pedida!');
            return;
          }
          pedidoParaEnviar.quantidadeEntregue = novaQuantidadeEntregue;
          setPedidoParaConcluir({ ...pedidoParaEnviar, quantidadeEntregueDigitada: novaQuantidadeEntregue - (pedidoParaEditar.quantidadeEntregue || 0) });
          setMostrarModalPesoVolume(true);
          setMensagem('Preencha peso e volume para salvar a edição.');
          return;
        }
        resposta = await axios.put(`http://localhost:5000/pedidos/${pedidoParaEditar.id}`, pedidoParaEnviar);
        const pedidoAtualizado = { ...resposta.data, tempo: calcularTempo(resposta.data.inicio, resposta.data.dataConclusao) };
        await axios.post('http://localhost:5000/enviar-email', { pedido: pedidoAtualizado, observacao: '' });
        atualizarListas(pedidoAtualizado);
        setMensagem('Pedido atualizado e e-mail enviado!');
        setPedidoParaEditar(null);
      } else {
        resposta = await axios.post('http://localhost:5000/pedidos', pedidoParaEnviar);
        const novoPedidoAdicionado = { ...resposta.data, tempo: calcularTempo(resposta.data.inicio) };
        await axios.post('http://localhost:5000/enviar-email', { pedido: novoPedidoAdicionado, observacao: '' });
        atualizarListas(novoPedidoAdicionado);
        setMensagem('Pedido adicionado e e-mail enviado!');
      }

      resetForm(); // Limpar os campos após sucesso
    } catch (error) {
      setMensagem('Erro ao processar pedido: ' + (error.response?.data.message || error.message));
      await carregarPedidos();
    }
  };

  const calcularTempo = (inicio, fim = new Date().toLocaleString('pt-BR', { timeZone: 'America/Sao_Paulo' })) => {
    const diffMs = new Date(fim) - new Date(inicio);
    return diffMs < 0 ? 0 : diffMs / (1000 * 60 * 60);
  };

  const atualizarListas = (pedido) => {
    if (pedido.status === 'novo') {
      setPedidosAndamento(prev => [...prev.filter(p => p.id !== pedido.id), pedido]);
      setPedidos(prev => prev.filter(p => p.id !== pedido.id));
      setPedidosConcluidos(prev => prev.filter(p => p.id !== pedido.id));
    } else if (pedido.status === 'andamento') {
      setPedidos(prev => [...prev.filter(p => p.id !== pedido.id), pedido]);
      setPedidosAndamento(prev => prev.filter(p => p.id !== pedido.id));
      setPedidosConcluidos(prev => prev.filter(p => p.id !== pedido.id));
    } else if (pedido.status === 'concluido') {
      setPedidosConcluidos(prev => [...prev.filter(p => p.id !== pedido.id), pedido]);
      setPedidos(prev => prev.filter(p => p.id !== pedido.id));
      setPedidosAndamento(prev => prev.filter(p => p.id !== pedido.id));
    }
  };

  const resetForm = () => {
    setNovoPedido({
      empresa: '',
      numeroOS: '',
      dataEntrada: '',
      previsaoEntrega: '',
      responsavel: '',
      codigoDesenho: '',
      quantidadePedido: '',
      quantidadeEntregue: '',
      status: 'novo',
      inicio: new Date().toLocaleString('pt-BR', { timeZone: 'America/Sao_Paulo' }),
      tempo: 0,
      peso: null,
      volume: null,
      dataConclusao: null,
    });
    setMostrarFormulario(false);
  };

  return (
    <form onSubmit={handleSubmit} className="formulario">
      <div>
        <label htmlFor="empresa">Empresa *</label>
        <input type="text" id="empresa" name="empresa" value={novoPedido.empresa} onChange={e => setNovoPedido({ ...novoPedido, empresa: e.target.value })} required />
      </div>
      <div>
        <label htmlFor="numeroOS">Número da OS *</label>
        <input type="text" id="numeroOS" name="numeroOS" value={novoPedido.numeroOS} onChange={e => setNovoPedido({ ...novoPedido, numeroOS: e.target.value })} required />
      </div>
      <div>
        <label htmlFor="codigoDesenho">Código do Desenho</label>
        <input type="text" id="codigoDesenho" name="codigoDesenho" value={novoPedido.codigoDesenho} onChange={e => setNovoPedido({ ...novoPedido, codigoDesenho: e.target.value })} />
      </div>
      <div>
        <label htmlFor="quantidadePedido">Quantidade Pedido</label>
        <input type="number" id="quantidadePedido" name="quantidadePedido" value={novoPedido.quantidadePedido} onChange={e => setNovoPedido({ ...novoPedido, quantidadePedido: e.target.value })} min="0" required />
      </div>
      <div>
        <label htmlFor="quantidadeEntregue">Quantidade Entregue</label>
        <input type="number" id="quantidadeEntregue" name="quantidadeEntregue" value={novoPedido.quantidadeEntregue} onChange={e => setNovoPedido({ ...novoPedido, quantidadeEntregue: e.target.value })} min="0" />
      </div>
      <div>
        <label htmlFor="dataEntrada">Data de Entrada *</label>
        <input type="date" id="dataEntrada" name="dataEntrada" value={novoPedido.dataEntrada} onChange={e => setNovoPedido({ ...novoPedido, dataEntrada: e.target.value })} required />
      </div>
      <div>
        <label htmlFor="previsaoEntrega">Previsão de Entrega *</label>
        <input type="date" id="previsaoEntrega" name="previsaoEntrega" value={novoPedido.previsaoEntrega} onChange={e => setNovoPedido({ ...novoPedido, previsaoEntrega: e.target.value })} required />
      </div>
      <div>
        <label htmlFor="responsavel">Responsável</label>
        <input type="text" id="responsavel" name="responsavel" value={novoPedido.responsavel} onChange={e => setNovoPedido({ ...novoPedido, responsavel: e.target.value })} />
      </div>
      <div>
        <label htmlFor="status">Status</label>
        <select id="status" name="status" value={novoPedido.status} onChange={e => setNovoPedido({ ...novoPedido, status: e.target.value })}>
          <option value="novo">Novo</option>
          <option value="andamento">Em Andamento</option>
          <option value="concluido">Concluído</option>
        </select>
      </div>
      <button type="submit">{pedidoParaEditar ? 'Salvar Alterações' : 'Adicionar Pedido'}</button>
      <button type="button" onClick={resetForm}>Cancelar</button>
    </form>
  );
};

export default PedidoForm;
